// src/index.tsx
import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

// Make sure this path matches exactly where ChatContext.tsx lives:
import { ChatProvider } from './contexts/ChatContext';

// Make sure these paths match exactly where your components live:
import ApplicationPage from './components/ApplicationPage';
import Dashboard from './components/dashboard';
import { AzureTopBar } from './components/azuretopbar';

const root = ReactDOM.createRoot(document.getElementById('root')!);

root.render(
  <React.StrictMode>
    <BrowserRouter>
      {/* Everything that needs `useChat()` must be inside <ChatProvider> */}
      <ChatProvider>
        {/* TopBar + Sidebar do notâ€”unless they also call useChat() */}
        <AzureTopBar />

        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/app1" element={<ApplicationPage />} />
          <Route path="/app2" element={<ApplicationPage />} />
          <Route path="/app3" element={<ApplicationPage />} />
          <Route path="/app4" element={<ApplicationPage />} />
        </Routes>
      </ChatProvider>
    </BrowserRouter>
  </React.StrictMode>
);
