// App.tsx
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AzureTopBar } from './components/azuretopbar';
import Dashboard from './components/dashboard';
import ApplicationPage from './components/ApplicationPage';
import { useState } from 'react';

const [isCollapsed, setIsCollapsed] = useState(true);
const sidebarWidth = isCollapsed ? 48 : 240;
export const App: React.FC = () => (
  <Router>
    {/* TopBar + Sidebar */}
    <AzureTopBar
      collapsed={isCollapsed}
      onToggle={() => setIsCollapsed(prev => !prev)}
    />

    {/* main content (Routes) */}
    <div style={{ marginLeft: sidebarWidth, flex: 1, padding: 2 }}>
      <div style={{ marginLeft: 48/* match sidebar width */, 
        padding: 2, width: '100%' }}>
        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/app1" element={<ApplicationPage />} />
          <Route path="/app2" element={<ApplicationPage />} />
          <Route path="/app3" element={<ApplicationPage />} />
          <Route path="/app4" element={<ApplicationPage />} />
        </Routes>
      </div>
    </div>
  </Router>
);

export default App;
