// src/components/azuresidebar.tsx
import React, { forwardRef } from 'react';
import { Stack, Icon } from '@fluentui/react';
import { useNavigate } from 'react-router-dom';
import styles from './azuresidebar.module.css';

const homeIcon = { iconName: 'Home' };
const shieldIcon = { iconName: 'Shield' };
const bankIcon = { iconName: 'Bank' };
const lockIcon = { iconName: 'Lock' };
const moneyIcon = { iconName: 'Money' };

const SIDEBAR_EXPANDED_WIDTH = 200;
const SIDEBAR_COLLAPSED_WIDTH = 48;

interface SidebarProps {
  collapsed: boolean;
  onToggle: () => void;
}

// Forward the ref so parent can detect clicks outside
export const Sidebar = forwardRef<HTMLDivElement, SidebarProps>(
  ({ collapsed, onToggle }, ref) => {
    const navigate = useNavigate();

    return (
      <div
        // Attach the forwarded ref here:
        ref={ref}
        className={styles.sidebarContainer}
        style={{
          width: collapsed ? SIDEBAR_COLLAPSED_WIDTH : SIDEBAR_EXPANDED_WIDTH,
        }}
      >
        {/* ── HEADER AREA ── */}
        <div
          className={
            collapsed
              ? `${styles.headerArea} ${styles.headerAreaCollapsed}`
              : `${styles.headerArea} ${styles.headerAreaExpanded}`
          }
          onClick={onToggle}
          title="Toggle navigation"
        >
          <Icon
            iconName="GlobalNavButton"
            className={
              collapsed
                ? `${styles.sidebarHamburger} ${styles.sidebarHamburgerCollapsed}`
                : `${styles.sidebarHamburger} ${styles.sidebarHamburgerExpanded}`
            }
          />
        </div>

        {/* ── ITEMS BELOW ── */}
        <Stack tokens={{ childrenGap: 4 }} styles={{ root: { paddingTop: 0 } }}>
          <div className={styles.sidebarItem} onClick={() => navigate('/')}>
            <Icon iconName={homeIcon.iconName!} className={styles.sidebarIcon} />
            {!collapsed && <span className={styles.sidebarLabel}>Home</span>}
          </div>
          <div className={styles.sidebarItem} onClick={() => navigate('/app1')}>
            <Icon iconName={shieldIcon.iconName!} className={styles.sidebarIcon} />
            {!collapsed && <span className={styles.sidebarLabel}>App 1</span>}
          </div>
          <div className={styles.sidebarItem} onClick={() => navigate('/app2')}>
            <Icon iconName={bankIcon.iconName!} className={styles.sidebarIcon} />
            {!collapsed && <span className={styles.sidebarLabel}>App 2</span>}
          </div>
          <div className={styles.sidebarItem} onClick={() => navigate('/app3')}>
            <Icon iconName={lockIcon.iconName!} className={styles.sidebarIcon} />
            {!collapsed && <span className={styles.sidebarLabel}>App 3</span>}
          </div>
          <div className={styles.sidebarItem} onClick={() => navigate('/app4')}>
            <Icon iconName={moneyIcon.iconName!} className={styles.sidebarIcon} />
            {!collapsed && <span className={styles.sidebarLabel}>App 4</span>}
          </div>
        </Stack>
      </div>
    );
  }
);
